import { Component } from 'react'

class AddItem extends Component {

    state = { quantity: 0, product: {} }

    updateQuantity = e => quantity = parseInt(e.target.value)

    updateProduct = e => product = JSON.parse(e.target.value)

    render({ products, onSubmit }) {

        return (
            <div className="container">
                <label className="mt-5 mb-2">Quantity</label>
                <input className="form-control" type="number" name="quantityInput" onChange={e => updateQuantity(e)} />

                <label className="mt-2 mb-2">Products</label>
                <select className="form-control" id="products" select="none" name="productSelection" onChange={e => updateProduct(e)} >
                    <option disabled selected value> -- select a product -- </option>
                    {products.map(product => <option key={product.id} value={JSON.stringify(product)}>{product.name}</option>)}
                </select>

                <button className="btn btn-primary mt-3 mb-3" type="submit" onClick={e => onSubmit(e, { product, quantity })} >Submit</button>
            </div>
        )
    }

}



// TODO reset quantity , pdouct and textfields after submit



export default AddItem
