let quantityValue = 0
let selectedProduct = {}

prepareProduct = (product, quantity) => {
    return {product, quantity}
}

const AddItem = ({ products, onSubmit }) => (
    <div>
        <p>Quantity</p>
        <input type="text" name="quantityInput" onChange={e => quantityValue + parseInt(e.target.value)} />
        <p>Products</p>
        <select id="products" name="productSelection" onChange={e => selectedProduct = e.target.value}>
            {products.map(product => <option value={product}>{product.name}</option>)}
        </select>

        <button type="submit" onClick={e => onSubmit(e, prepareProduct(selectedProduct, quantity))} name="Submit" />
    </div>)

export default AddItem
